SELECT * FROM empleado WHERE apellido2 = 'Díaz' OR apellido2 = 'Moreno';
SELECT * FROM empleado WHERE apellido2 IN ('Díaz' , 'Moreno');
SELECT nombre, apellido1, apellido2, nif FROM empleado WHERE codigo_departamento = 3;
SELECT nombre, apellido1, apellido2, nif FROM empleado WHERE codigo_departamento IN(2, 4, 5);
SELECT empleado.nombre, departamento.codigo,departamento.nombre, presupuesto,gastos FROM empleado LEFT JOIN departamento ON codigo_departamento = departamento.codigo;
SELECT empleado.nombre, departamento.codigo,departamento.nombre, presupuesto,gastos FROM empleado LEFT JOIN departamento ON codigo_departamento = departamento.codigo ORDER BY departamento.nombre, apellido1, apellido2, empleado.nombre ASC;
SELECT DISTINCT departamento.codigo,departamento.nombre FROM departamento LEFT JOIN empleado ON codigo_departamento = departamento.codigo WHERE codigo_departamento IS NOT NULL;
SELECT DISTINCT departamento.codigo, departamento.nombre, presupuesto-gastos AS presupuesto_actual FROM departamento INNER JOIN empleado ON codigo_departamento = departamento.codigo WHERE codigo_departamento IS NOT NULL;
SELECT departamento.nombre FROM departamento LEFT JOIN empleado ON codigo_departamento = departamento.codigo WHERE nif = '38382980M';
SELECT departamento.nombre FROM departamento LEFT JOIN empleado ON codigo_departamento = departamento.codigo WHERE empleado.nombre ='Pepe' AND apellido1 = 'Ruiz' AND apellido2 ='Santana';
SELECT * FROM empleado LEFT JOIN departamento ON codigo_departamento = departamento.codigo WHERE departamento.nombre = 'I+D' ORDER BY empleado.nombre ASC;
SELECT * FROM empleado LEFT JOIN departamento ON codigo_departamento = departamento.codigo WHERE departamento.nombre IN ('Sistemas', 'Contabilidad', 'I+D') ORDER BY empleado.nombre ASC;
SELECT empleado.nombre FROM empleado LEFT JOIN departamento ON codigo_departamento = departamento.codigo WHERE presupuesto NOT BETWEEN 100000 AND 200000;
SELECT  DISTINCT departamento.nombre FROM departamento INNER JOIN empleado ON codigo_departamento = departamento.codigo WHERE apellido2 IS NULL;
SELECT  empleado.nombre AS empleado, departamento.nombre AS departamento FROM empleado LEFT JOIN departamento ON codigo_departamento = departamento.codigo;
SELECT  empleado.nombre AS empleado, departamento.nombre AS departamento FROM empleado LEFT JOIN departamento ON codigo_departamento = departamento.codigo WHERE departamento.nombre IS NULL;
SELECT  departamento.nombre AS departamento, empleado.nombre AS empleado FROM departamento LEFT JOIN empleado ON codigo_departamento = departamento.codigo WHERE empleado.nombre IS NULL;
((select e1.codigo as 'Codigo Empleado', e1.nombre as 'Nombre Empleado', d1.codigo as 'Codigo Departamento', d1.nombre as 'Nombre Departamento' from empleado e1 left join departamento d1 on e1.codigo_departamento=d1.codigo or d1.codigo is null)
union
(select null as 'Codigo Empleado', null as 'Nombre Empleado', d2.codigo as 'Codigo Departamento', d2.nombre as 'Nombre Departamento' from departamento d2 left join empleado e2 on e2.codigo_departamento=d2.codigo where not exists (select * from empleado e3 where e3.codigo_departamento=d2.codigo))) order by 'Nombre Departamento';

(select e1.codigo as 'Codigo Empleado', e1.nombre as 'Nombre Empleado', d1.codigo as 'Codigo Departamento', d1.nombre as 'Nombre Departamento' from empleado e1 left join departamento d1 on e1.codigo_departamento=d1.codigo where not exists (select * from departamento d3 where e1.codigo_departamento=d3.codigo))
union
(select null as 'Codigo Empleado', null as 'Nombre Empleado', d2.codigo as 'Codigo Departamento', d2.nombre as 'Nombre Departamento' from departamento d2 left join empleado e2 on e2.codigo_departamento=d2.codigo where not exists (select * from empleado e3 where e3.codigo_departamento=d2.codigo));
