SELECT codigo_departamento, departamento.nombre, COUNT(empleado.nombre) AS numero_empleado FROM empleado LEFT JOIN departamento ON codigo_departamento = departamento.codigo GROUP BY codigo_departamento;
SELECT COUNT(empleado.nombre) AS numero_empleado FROM empleado INNER JOIN departamento ON codigo_departamento = departamento.codigo WHERE presupuesto>200000;
