SELECT nombre FROM departamento WHERE codigo NOT IN (SELECT codigo_departamento FROM empleado WHERE codigo_departamento = departamento.codigo);
SELECT nombre FROM departamento WHERE EXISTS  (SELECT codigo_departamento FROM empleado WHERE codigo_departamento = departamento.codigo);
SELECT nombre FROM departamento WHERE NOT EXISTS  (SELECT codigo_departamento FROM empleado WHERE codigo_departamento = departamento.codigo);